opub.status.busy":"2022-12-29T07:41:22.457445Z","iopub.execute_input":"2022-12-29T07:41:22.458819Z","iopub.status.idle":"2022-12-29T07:41:47.561135Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Create individual columns for month day and year\ndata$day <- factor(day(data$Date.Time))\ndata$month <- factor(month(data$Date.Time, label=TRUE))\ndata$year <- factor(year(data$Date.Time))\ndata$dayofweek <- factor(wday(data$Date.Time, label=TRUE))\nhead(data)","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:41:50.740422Z","iopub.execute_input":"2022-12-29T07:41:50.741784Z","iopub.status.idle":"2022-12-29T07:42:04.599136Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Add Time variables as well \ndata$second = factor(second(hms(data$Time)))\ndata$minute = factor(minute(hms(data$Time)))\ndata$hour = factor(hour(hms(data$Time)))\nhead(data)","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:43:30.247505Z","iopub.execute_input":"2022-12-29T07:43:30.249638Z","iopub.status.idle":"2022-12-29T07:43:44.895244Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"markdown","source":"## Data Visualisation","metadata":{}},{"cell_type":"code","source":"# Plotting the trips by hours in a day\nhourly_data <- data %>% \n  group_by(hour) %>% \n  dplyr::summarize(Total = n())","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:45:09.364903Z","iopub.execute_input":"2022-12-29T07:45:09.367265Z","iopub.status.idle":"2022-12-29T07:45:09.709845Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Show data in a searchable js table\ndatatable(hourly_data)","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:45:18.927947Z","iopub.execute_input":"2022-12-29T07:45:18.93013Z","iopub.status.idle":"2022-12-29T07:45:19.216236Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Plot the data by hour\nggplot(hourly_data, aes(hour, Total)) + \n  geom_bar(stat=\"identity\", \n           fill=\"steelblue\", \n           color=\"red\") + \n  ggtitle(\"Trips Every Hour\", subtitle = \"aggregated today\") + \n  theme(legend.position = \"none\", \n        plot.title = element_text(hjust = 0.5), \n        plot.subtitle = element_text(hjust = 0.5)) + \n  scale_y_continuous(labels=comma)","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:45:34.748527Z","iopub.execute_input":"2022-12-29T07:45:34.749938Z","iopub.status.idle":"2022-12-29T07:45:35.331204Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Plotting trips by hour and month\n# Aggregate the data by month and hour\nmonth_hour_data <- data %>% group_by(month, hour) %>%  dplyr::summarize(Total = n())\n\nggplot(month_hour_data, aes(hour, Total, fill=month)) + \n  geom_bar(stat = \"identity\") + \n  ggtitle(\"Trips by Hour and Month\") + \n  scale_y_continuous(labels = comma)","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:49:25.571479Z","iopub.execute_input":"2022-12-29T07:49:25.57298Z","iopub.status.idle":"2022-12-29T07:49:26.595408Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Plotting data by trips during every day of the month\n# Aggregate data by day of the month \nday_data <- data %>% group_by(day) %>% dplyr::summarize(Trips = n())\nday_data","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:51:01.804594Z","iopub.execute_input":"2022-12-29T07:51:01.806103Z","iopub.status.idle":"2022-12-29T07:51:01.982276Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Plot the data for the day\nggplot(day_data, aes(day, Trips)) + \n  geom_bar(stat = \"identity\", fill = \"steelblue\") +\n  ggtitle(\"Trips by day of the month\") + \n  theme(legend.position = \"none\") + \n  scale_y_continuous(labels = comma)","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:51:17.044832Z","iopub.execute_input":"2022-12-29T07:51:17.046921Z","iopub.status.idle":"2022-12-29T07:51:17.260517Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Collect data by day of the week and month\nday_month_data <- data %>% group_by(dayofweek, month) %>% dplyr::summarize(Trips = n())\n\nday_month_data","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:51:40.169093Z","iopub.execute_input":"2022-12-29T07:51:40.171427Z","iopub.status.idle":"2022-12-29T07:51:40.399636Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Plot the Collect data by day of the week and month\nggplot(day_month_data, aes(dayofweek, Trips, fill = month)) + \n  geom_bar(stat = \"identity\", aes(fill = month), position = \"dodge\") + \n  ggtitle(\"Trias by Day and Month\") + \n  scale_y_continuous(labels = comma) + \n  scale_fill_manual(values = colors)","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:52:03.720072Z","iopub.execute_input":"2022-12-29T07:52:03.722128Z","iopub.status.idle":"2022-12-29T07:52:04.240716Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Number of Trips place during months in a year\nmonth_data <- data %>% group_by(month) %>% dplyr::summarize(Total = n())\n\nmonth_data","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:52:15.22813Z","iopub.execute_input":"2022-12-29T07:52:15.232336Z","iopub.status.idle":"2022-12-29T07:52:15.393578Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Plot the Number of Trips place during months in a year\nggplot(month_data, aes(month, Total, fill = month)) + \n  geom_bar(stat = \"Identity\") + \n  ggtitle(\"Trips in a month\") + \n  theme(legend.position = \"none\") + \n  scale_y_continuous(labels = comma) + \n  scale_fill_manual(values = colors)","metadata":{"execution":{"iopub.status.busy":"2022-12-29T07:52:30.466023Z","iopub.execute_input":"2022-12-29T07:52:30.4